var hvacData = ['A0', 'A1', 'A2', 'A3', 'A', 'B0', 'B1', 'B2', 'B3', 'B', 'C0', 'C1', 'C2', 'C3', 'C', 'D0', 'D1', 'D2', 'D3', 'D'];

var vents = scene.selectAll("a-obj-model.hvac").data(hvacData)

vents.enter().append("a-obj-model")
  .classed("hvac", true)
  .merge(vents)
  .attrs({
      src: function (d) {return "#MKT-" + d},
      color: function (d, i) {return d3.interpolateInferno(map(i, 0, 20, 0, 1))}
      })
  .on("mouseenter", function() {
      if(this.hovering) return;
      this.hovering = true;
      d3.select(this).transition().duration(1000)
        .attrs({
            color: "white"
          })
        })
  .on("mouseleave", function(d, i) {
      this.hovering = false;
      d3.select(this).transition().duration(1000)
        .attrs({
            color: function () {return d3.interpolateInferno(map(i, 0, 20, 0, 1))}
          })
        });

// adjust static class properties
scene.selectAll(".bldg").attrs({
      color: "grey",
      opacity: .85
    });

scene.selectAll(".structure").attrs({
      color: "white",
      opacity: .85
    });

scene.selectAll(".shell").attrs({
      color: "#444",
      opacity: 1
    });

scene.selectAll(".roof").attrs({
      color: "#444",
      opacity: 1
    });

update(positions);

function update(data) {

var points = scene.selectAll("a-sphere").data(data, function(d) { return d; });

points.enter().append("a-sphere")
.attrs({
  position: function(d) {
    var x=d.x+16;
    var y=d.y;
    var z=d.z+16;
    return x + " " + y + " " + z
    },
  radius: .125,
  color: function(d, i) {
    return d3.interpolateCool(map(i, 0, 216, 0, 1))}
});
}

function map(value, low1, high1, low2, high2) {
    return low2 + (high2 - low2) * (value - low1) / (high1 - low1);
}
